// 使用 Player 对象 (便利方法，自动获取 UserId)
var result = await CloudDataApi.ForPlayer(player).SetData("level", 25).ExecuteAsync();
var listItems = await CloudDataApi.QueryPlayerListItemsAsync(player, "inventory");

