// 简单的数值操作
var result = await TransactionBuilder
    .ForUser(playerId)
    .AddCurrency("gold", 100)
    .CostCurrency("energy", 10)
    .SetData("level", 25)
    .WithDescription("Player level up reward")
    .ExecuteAsync();

// 包含列表项操作的复杂事务
var builder = TransactionBuilder.ForUser(playerId);
var weaponRef = builder.PrepareListItem("inventory", weaponData);

var result2 = await builder
    .AddListItem(weaponRef)
    .AddCurrency("gold", 500)
    .SetData("last_reward", DateTime.UtcNow.ToString())
    .WithDescription("Quest completion reward")
    .ExecuteAsync();

// 使用生成的weapon ID
var weaponId = weaponRef.Id;

// 准备阶段 - 生成ID和引用
var itemRef = builder.PrepareListItem("inventory", itemData);
var itemId = itemRef.Id; // 获取生成的ID

// 执行阶段 - 将列表项添加到事务中
builder.AddListItem(itemRef);

// 在同一个事务中可以更新刚添加的项目
builder.UpdateListItem(itemRef, newData);

// 将物品从背包移动到仓库
builder.MoveListItem(itemId, "warehouse");

// 将装备从背包移动到装备栏
builder.MoveListItem(equipmentId, "equipped_items");

// 批量准备引用
var itemRefs = builder.PrepareListItems("inventory", weaponData, armorData, potionData);

// 批量添加到事务
builder.AddListItems(itemRefs);

// 批量准备引用
var itemRefs = builder.PrepareListItems("inventory", weaponData, armorData, potionData);

// 批量添加到事务
builder.AddListItems(itemRefs)
       .WithDescription("Batch loot acquisition");

// 消耗10点体力，上限100，每日0点重置
builder.ModifyCappedData("energy", 10, 100, UserDataResetOption.Daily());

// 补充5点体力，注意对于体力这种数据，实际上保存的值是已经消耗的体力，所以这里需要传入负数。
// 如果觉得这一逻辑很奇怪，请记住有上限数据在重置时是重置为0而不是上限值。
builder.ModifyCappedData("energy", -5, 100);

// 设置新的上限值
builder.ModifyCappedData("energy", 0, 120);

// 重置体力数据（将已消耗体力重置为0，即恢复满体力）
builder.ResetCappedData("energy_consumed");

// 重置每日任务进度
builder.ResetCappedData("daily_quest_progress");

// 删除公会名称
builder.DeleteName("guild_names", "AwesomeGuild");

// 删除玩家昵称
builder.DeleteName("player_nicknames", "CoolPlayer123");

